import React, { useState } from "react";
import { IProduct } from "../models";
import axios from "axios";
import { ErrorMessage } from "./ErrorMessage";

const productData: IProduct = {
    title: '',
    price: 13.5,
    description: 'lorem ipsum set',
    image: 'https://i.pravatar.cc',
    category: 'electronic',
    rating: {
        rate: 42,
        count: 10
    }
}

interface CreateProductProps {
    onCreate: (product: IProduct) => void
}

export function CreateProduct({ onCreate }: CreateProductProps) {

    const [value, setValue] = useState('')
    const [error, setError] = useState('')


    const submitHandler = async (event: React.FormEvent) => {
        event.preventDefault()
        setError('')

        if (value.trim().length === 0) {
            setError('Please enter valid title.')
            return
        }

        productData.title = value

        const response = await axios.post<IProduct>('https://fakestoreapi.com/products', productData)

        onCreate(response.data)
    }

    return(
        <form onSubmit={submitHandler} className="max-w-md mx-auto mt-6 p-6 bg-white rounded-lg shadow-md">
        <input
    type="text" 
    className="w-full p-3 mb-4 border border-gray-300 rounded focus:outline-none focus:ring focus:border-blue-400"
    placeholder="Enter product title..."
    value={value}
    onChange={(event) => setValue(event.target.value)}
  />

  {error && <ErrorMessage error={error} className="mb-4" />}

  <button
    type="submit"
    className="w-full py-3 bg-yellow-400 hover:bg-yellow-500 text-white font-semibold rounded transition duration-300"
  >
    Create
  </button>
</form>

