import React from "react";
import { useContext } from 'react';
import { CreateProduct } from "../components/CreateProduct";
import { ErrorMessage } from "../components/ErrorMessage";
import { Modal } from "../components/Modal";
import { Product } from "../components/Product";
import { Loader } from "../components/loader";
import { useProducts } from "../hooks/products";
import { IProduct } from "../models";
import { ModalContext } from "../context/ModalContext";


export function ProductsPage() {
    const {loading, error, products, addProduct} = useProducts()
    const {close, open, modal} = useContext(ModalContext)

    const createHandler = (product: IProduct) => {
        close()
        addProduct(product)
    }
    

    return (
        <div className="container mx-auto max-w-2xl pt-10 pb-16 px-6">
            {loading && <Loader />}
            {error && <ErrorMessage error={error} />}
        
            {products.map((product) => (
                <Product product={product} key={product.id} />
            ))}
        
            {modal && (
                <Modal title="Create new product" onClose={close}>
                <CreateProduct onCreate={createHandler} />
                </Modal>
            )}
        
            {/* <button
                className="fixed bottom-10 right-10 rounded-full bg-red-700 text-white text-3xl px-6 py-3 shadow-lg hover:shadow-xl transition duration-300"
                onClick={open}
            >
                +
            </button> */}
        </div>
    );
}
